Templates.template:
createDraftFromActiveDocument(
	"${oContext}", // sap.ui.model.odata.v4.Context
	{
		bPreserveChanges: ${bBPreserveChanges}, // boolean
		fnBeforeCreateDraftFromActiveDocument: ${fnBeforeCreateDraftFromActiveDocument}, // sap.fe.actions.draft.ICallback.beforeCreateDraftFromActiveDocument
		fnAfterCreateDraftFromActiveDocument: ${fnAfterCreateDraftFromActiveDocument}, // sap.fe.actions.draft.ICallback.afterCreateDraftFromActiveDocument
		fnWhenDecisionToOverwriteDocumentIsRequired: ${fnWhenDecisionToOverwriteDocumentIsRequired} // sap.fe.actions.draft.ICallback.whenDecisionToOverwriteDocumentIsRequired
	}
)

callActionImport(
	"${sActionName}", // string
	"${oModel}", // sap.ui.model.odata.v4.ODataModel
	{
		actionParameters: ${actionParameters}, // map
		label: "${label}", // string
		showActionParameterDialog: ${bShowActionParameterDialog}, // boolean
		onSubmitted: ${fn_onSubmitted} // function
	}
)

navigateToContext(
	"${errorMessage}", // string
	{
		navContainer: ${navContainer}, // sap.m.NavContainer
		description: "${description}", // string
		technicalMessage: "${technicalMessage}", // string
		technicalDetails: "${technicalDetails}" // string
	}
)

createDocument(
	"${OData}", // sap.ui.model.odata.v4.ODataListBinding
	{
		refreshList: ${bRefreshList}, // boolean
		data: ${data}, // map
		busyMode: "${busyMode}", // string
		keepTransientContextOnFailed: ${keepTransientContextOnFailed}, // map
		busyHandling: "${busyHandling}" // string
	}
)

onCallAction(
	"${sActionName}", // string
	{
		contexts: ${contexts}, // sap.ui.model.odata.v4.Context
		model: ${model}, // sap.ui.model.odata.v4.ODataModel
		invocationGrouping: "${invocationGrouping}", // string
		label: "${label}" // string
	}
)
